@Component({
  selector: 'app-hello-world',
  templateUrl: './hello-world.component.html',
  styleUrls: ['./hello-world.component.css']
})
export class HelloWorldComponent {
  title = 'List of movies';

  movies =[ 
    {title:'a',director:'vishu',releaseDate:'March 4 ,3033'},
    {title:'b',director:'fdf',releaseDate:'jan 4 ,2011'},
    {title:'c',director:'efew',releaseDate:'jul 4 ,2022'},
    {title:'d',director:'hrthr',releaseDate:'aug 5 ,332'}

  ]
}
