import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
@Component({
  selector: 'app-template-driven-form',
  templateUrl: './template-driven-form.component.html',
  styleUrls: ['./template-driven-form.component.css']
})
export class TemplateDrivenFormComponent implements OnInit {
  countryList:Country[]= [
    new Country('1','India'),
    new Country('2', 'USA'),
    new Country('3', 'England'),
    new Country('4', 'Sri Lankha'),
    new Country('5', 'Russia')
  ];

  contact!:Contact;

  ngOnInit(): void {
    this.contact= {
      firstName: "Sachin",
      lastName: "Tendulkar",
      email: "abinesh@gmail,com",
      gender: "male",
      isMarried: false,
      country: "1",
      address: { city: "mumbai", street:"katpadi", pincode:"45433"}
    }
  }
  onSubmit(form: NgForm) {
    console.log(form.value);
  }
}
class Country {
  id:string;
  name:string;

  constructor(id:string, name:string) {
    this.id=id;
    this.name= name;
  }
}

class Contact {
  firstName!: string;
  lastName!: string;
  email!: string;
  gender!: string;
  isMarried!: boolean;
  country!: string;
  address!: {
    city:string;
    street: string;
    pincode:string;
  }
}

<div class="container">
    <h3>Template Driven Form</h3>
    <form #contactForm="ngForm" (submit)="onSubmit(contactForm)">
        <p>
            <label for="firstname">First Name </label>
            <input type="text" id="firstname" name="firstname" [(ngModel)]="contact.firstName">
        </p>

        <p>
            <label for="lastname">Last Name</label>
            <input type="text" id="lastname" name="lastname" ngModel>
        </p>

        <p>
            <label for="email">Email </label>
            <input type="text" name="email" id="email" ngModel>
        </p>

        <p>
            <label for="gender">Gender</label>
            <input type="radio" value="male" id="gender" name="gender" ngModel>Male
            <input type="radio" value="female" id="gender" name="gender" ngModel>Female
        </p>

        <p>
            <label for="isMarried">Married</label>
            <input type="checkbox" id="isMarried" name="isMarried" ngModel>
        </p>

        <p>
            <label for="country">country</label>
            <select name="country" id="country" ngModel>
                <option value="">Select</option>
                <option [ngValue]="country.id" *ngFor="let country of countryList">
                    {{country.name}}
                </option>
            </select>
        </p>

        <div ngModelGroup="address">
            <p>
                <label for="city">City</label>
                <input type="text" name="city" ngMode>
            </p>

            <p>
                <label for="street">Street</label>
                <input type="text" name="street" ngModel>
            </p>

            <p>
                <label for="pincode">Pin code</label>
                <input type="text" name="pincode" ngModel>
            </p>

        </div>

        <p>
            <button type="submit">Submit</button>
        </p>

    </form>
</div>
